<?php	class ImageResizer extends Controller {				public static function before($request, $vars = Array()) {					}				public static function resize($request, $vars = Array()) {			$filePath = $vars['file_path'];			if (Utils::isImage($filePath)) {				$width = $vars['width'];				$height = $vars['height'];								require_once app_root . ds . "libs" . ds . "phpthumb" . ds . "ThumbLib.inc.php";								$phpthumb = PhpThumbFactory::create($filePath);				$phpthumb->adaptiveResize($width, $height);				$phpthumb->show();								//$phpthumb->save($this->uploadFolder . ds . $this->resizeOptions[$i][0] . $to);				//unset($phpthumb);			}		}			}?>