<?php	class SessionStorage {		public static function startSession(){			if(!isset($_SESSION)) session_start();		}				public static function add($key, $value) {			self::startSession();			$_SESSION[$key] = $value;		}		public static function addFlash($key, $value){			self::startSession();			$_SESSION[$key . "_flash"] = $value;		}		public static function getFlash($key){			self::startSession();			if(isset($_SESSION[$key . "_flash"])) {				$tmp = $_SESSION[$key . "_flash"];				unset($_SESSION[$key . "_flash"]);				return $tmp;			}			else {				return null;			}		}				public static function get($key) {			self::startSession();			if (isset($_SESSION[$key])) {				return $_SESSION[$key];			}			else {				return false;			}		}				public static function edit($key, $value) {			self::startSession();			if (isset($_SESSION[$key])) {				$_SESSION[$key] = $value;				return true;			}			else {				return false;			}		}				public static function remove($key) {			self::startSession();			if (isset($_SESSION[$key])) {				unset($_SESSION[$key]);				return true;			} else {				return false;			}		}				public static function getStorageObject() {			self::startSession();			return $_SESSION;		}			}?>