<?php	class TreeViewModel extends CRUDModel {		public static $relatedModel;			public static function view($request, &$smarty, $vars = Array()) {			$items = self::find(" ORDER BY `order`, `parentId`");						$items = self::buildTree($items);			$treeItems = Array(				Array(				'id' => 0,				'parentId' => 0,				'title' => Application::$modules['admin']['static_url'] . '/dark_theme/img/tree-icon.png',				)			);			return Array(				'items' => array_merge($treeItems, $items),			);		}			public static function add($request, &$smarty, $vars = Array(), $saveItem = true, $model = false) {			$className = get_called_class();			$className::initialize();			$relatedModel = self::$relatedModel;			return $relatedModel::add($request, $smarty, $vars, $saveItem, $relatedModel);		}				public static function edit($request, &$smarty, $vars = Array(), $saveItem = false, $model = false) {			return parent::edit($request, $smarty, $vars, $saveItem, $model);		}				public static function getForm(&$smarty, $data = false, $lang = false) {					}				public static function deleteItem($request, &$smarty, $vars = Array(), $itemsToDelete = Array()) {					}	}?>