<?php	class ModelBooleanField extends ModelField {		// db synchronize settings		public $dbType = 'bit';		public $dbLength = 1;		// db synchronize settings end	        public function __construct($name, $title, $required, $multiLang, $common = false, $regExp = "/^.*/im") {            parent::__construct($name, $title, $required, $multiLang, $common, $regExp);        }        public function  getHTML($lang = false) {            return parent::generateHTML('textfield.tpl', $this->value, $lang);        }		    }?>