<?php	class ModelCheckBoxField extends ModelField {		public $data;				// valueType values : (char,int)		public $dbType = 'varchar';		// db settings end	        public function __construct($name, $title, $data, $required, $multiLang, $common = false, $regExp = "/^.*/im") {            parent::__construct($name, $title, $required, $multiLang, $common, $regExp);			$this->data = $data;        }				public function getDisplayValue() {			$keys = explode(",", $this->value);			$out = Array();			foreach ($keys as $k) {				if (in_array($k, array_keys($this->data))) $out[] = $this->data[$k];			}			return join(", ", $out);		}				public function  getHTML($lang = false) {            return parent::generateHTML('checkboxfield.tpl', explode(",", $this->value), $lang, $this->data);        }		    }?>