<?php	class ModelDateField extends ModelField {		// db settings		public $dbType = 'date';		// db settings end	        public function __construct($name, $title, $required, $multiLang, $common = false, $regExp = "/^.*/im") {            parent::__construct($name, $title, $required, $multiLang, $common, $regExp);        }        public function  getHTML($lang = false) {            return parent::generateHTML('datefield.tpl', $this->value, $lang);        }    }?>