<?php	class ModelPasswordField extends ModelField {		public $maxLength = 100;				// db synchronize settings		public $dbType = 'varchar';		// db synchronize settings end	        public function __construct($name, $title, $required, $multiLang, $common = false, $regExp = "/^.*/im") {            parent::__construct($name, $title, $required, $multiLang, $common, $regExp);        }        public function  getHTML($lang = false) {            return parent::generateHTML('passwordfield.tpl', $this->value, $lang);        }				public function setValue($value) {			$this->value = md5($value);		}				public function getValue() {			return null;		}				public function getDisplayValue() {			return 'psw';		}		    }?>