<?php	class ModelRadioField extends ModelField {		public $data;				// db settings		// valueType values : (char,int)		public $dbType = 'varchar';		// db settings end		        public function __construct($name, $title, $data, $required, $multiLang, $common = false, $regExp = "/^.*/im") {            parent::__construct($name, $title, $required, $multiLang, $common, $regExp);			$this->data = $data;        }				public function getDisplayValue() {			if (isset($this->data[$this->value])) return $this->data[$this->value];			else return "undefined";		}				public function  getHTML($lang = false) {            return parent::generateHTML('radiofield.tpl', $this->value, $lang, $this->data);        }    }?>