<?php	class ModelSelectField extends ModelField {		public $data;			// valueType values : (char,int)		public $dbType = 'int';		// db settings end		        public function __construct($name, $title, $data, $required, $multiLang, $common = false, $regExp = "/^.*/im", $tplFile = 'selectfield.tpl') {            parent::__construct($name, $title, $required, $multiLang, $common, $regExp);			$this->data = $data;			$this->tplFile = $tplFile;        }				public function getDisplayValue() {			if (isset($this->data[$this->value])) return $this->data[$this->value];			else return "undefined";		}				public function  getHTML($lang = false) {			$keys = array_keys($this->data);			$defaultValue = $keys[0];            return parent::generateHTML($this->tplFile, $this->value, $lang, $this->data, $defaultValue);        }    }?>