<?php	class ModelTextField extends ModelField {		public $maxLength = 100;		public $valueType = 'VARCHAR';				// db synchronize settings		public $dbType = 'varchar';		// db synchronize settings end	        public function __construct($name, $title, $required, $multiLang, $common = false, $regExp = "/^.*/im") {            parent::__construct($name, $title, $required, $multiLang, $common, $regExp);        }        public function  getHTML($lang = false) {            return parent::generateHTML('textfield.tpl', Security::filterString($this->value), $lang);        }		    }?>