<?php		class ModelTinyMce extends ModelField {		// valueType values : (char,int)		public $dbType = 'blob';		// db settings end		        public function __construct($name, $title, $required, $multiLang, $common = false, $regExp = "/^.*/im") {            parent::__construct($name, $title, $required, $multiLang, $common, $regExp);        }        public function  getHTML($lang = false) {            return parent::generateHTML('tinymce.tpl', $this->value, $lang);        }		    }	?>