<?php		class AdminUsersGroupModel extends CRUDModel {		public $groupTitle;		public $allowedModels;				public function __construct() {		Global $_adminName;			$this->groupTitle = new ModelTextField("groupTitle", Application::$messages['model_admin_users_group']['field_groupTitle'], true, false);			$this->allowedModels = new ModelCheckBoxField("allowedModels", Application::$messages['model_admin_users_group']['field_allowedModules'], Application::$modules[$_adminName]['models'], true, false);		}				public static function initialize() {			self::$title = Application::$messages['model_admin_users_group']['title'];			self::$iconPath = 'groups.png';			self::$displayFields = Array('groupTitle');			self::$useOwnViewUrl = false;		}	}	?>